import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  public start = 0;
  public amount = 0;

  public addNumbers() {

  }
  public subtractNumbers() {

  }
}
<h1 class="home">Simple Calulator</h1>
<p class="home">Enter two numbers below then click add or subtract</p>

<div class="home home__input">
  <label for="start">Start: </label>
  <input value="{{start}}" id="start" />
</div>
<div class="home home__input">
  <label for="amount">Amount: </label>
  <input value="{{amount}}" id="amount"/>
</div>

<button class="home home__button">Add</button><button class="home__button">Subtract</button>
<app-modal></app-modal>
