import { Component, Inject, ViewChild } from '@angular/core';
import { OverlayComponent } from '../overlay/overlay.component';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  @ViewChild(OverlayComponent) child!: OverlayComponent;
  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.baseUrl = baseUrl;
    this.http = http;
  }
  http: HttpClient;
  baseUrl: string;
  isOpen = false;

  updateStart(val:string)
  {
    this.start = val as unknown as number;
  }
  updateAmount(val: string) {
    this.amount = val as unknown as number;
  }
  start:number = 0;
  amount:number = 0;
  result = 0;

  headerColor = '#1C110A';
  bodyColor = '#E7E5DF';
  footerColor = '#1C110A'

  headerTextColor = '#FFFFFF';
  bodyTextColor = '#000000';
  footerTextColor = '#FFFFFF'
  subtract() {
  };
  add() {
    http.get<int>(baseUrl + 'weatherforecast').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));
  };

  open() {
    this.isOpen = !this.isOpen;
    this.child.toggleOpen();
  }
}

<app-overlay></app-overlay>

<div class="app_modal__modal {{isOpen ? '' :  'app_modal__modal--hidden'}}">
  <div class="app_modal__header" [ngStyle]="{'background-color': headerColor, 'color' : headerTextColor}">
    <h1>Simple Calulator</h1>
  </div>
  <div class="app_modal__container" [ngStyle]="{'background-color': bodyColor, 'color' : bodyTextColor}">
    <p class="home">Enter two numbers below then click add or subtract</p>

    <div class="app_modal__container-input">
      <label for="start">Start: </label>
      <input type="number" id="start" value="{{start}}" #start (blur)="updateStart(start.value)" />
    </div>
    <div class="app_modal__container-input">
      <label for="amount">Amount: </label>
      <input type="number"  id="amount" value="{{amount}}"  #amount (blur)="updateStart(amount.value)" />
    </div>

    <button class="app_modal__container-button" (click)="add()">Add</button>
    <button class="app_modal__container-button" (click)="subtract()">Subtract</button>

    <h2 class="app_modal__container-result">{{result}}</h2>

  </div>
  <div class="app_modal__footer" [ngStyle]="{'background-color': footerColor, 'color' : footerTextColor}"><span (click)="open()">Close</span></div>
</div>
