import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
}
<h1>Simple Calulator</h1>
<p>Enter two numbers below then click add or subtract</p>

<div class="home__input">
  <label for="start">Start: </label>
  <input value="" id="start" />
</div>
<div class="home__input">
  <label for="amount">Amount: </label>
  <input value="" id="amount"/>
</div>

<button class="home__button">Add</button><button>Subtract</button>
