import { Component, OnChanges, ViewChild } from '@angular/core';
import { ModalComponent } from '../modal/modal.component';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnChanges {
  @ViewChild(ModalComponent) child!: ModalComponent;

  headerColor = '#1C110A';
  bodyColor = '#E7E5DF';
  footerColor = '#1C110A'

  headerTextColor = '#FFFFFF';
  bodyTextColor = '#000000';
  footerTextColor = '#FFFFFF'

  public callModal() {
    // lazy
    this.child.headerColor = this.headerColor;
    this.child.bodyColor = this.bodyColor;
    this.child.footerColor = this.footerColor;
    this.child.headerTextColor = this.headerTextColor;
    this.child.bodyTextColor = this.bodyTextColor;
    this.child.footerTextColor = this.footerTextColor;


    this.child.open();
  }
}
<app-modal></app-modal>

<h1>Simple Calculator</h1>

<p>Please select the colors for your modal below, then the calculator button below.</p>
<div>
  <h4>Header</h4>
  <div>
    <label for="header">Background: </label>
    <input id="header" value="{{headerColor}}" type="color" />
  </div>
  <div>
    <label for="headerText">Text: </label>
    <input id="headerText" value="{{headerTextColor}}" type="color" />
  </div>
</div>
<div>
  <h4>Body</h4>
  <div>
    <label for="body">Background: </label>
    <input id="bodyText" value="{{bodyColor}}" type="color" />
  </div>
  <div>
    <label for="body">Text: </label>
    <input id="bodyText" value="{{bodyTextColor}}" type="color" />
  </div>
</div>
<div>
  <h4>Footer</h4>
  <div>
    <label for="footer">Background: </label>
    <input id="footer" value="{{footerColor}}" type="color" />
  </div>
  <div>
    <label for="footerText">Text: </label>
    <input id="footerText" value="{{footerTextColor}}" type="color" />
  </div>
</div>
<button (click)="callModal()">Calculator</button>
