import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  constructor() { }
  isOpen = false;
  header = '';
  body = '';
  footer = '';
  callback = ''

  public configure(header: string, body: string, footer: string, callback: Function) {

  }
}

<div class="app_modal__background {{isOpen ? '' : 'app_modal--hidden'}}">
  <div class="app_modal__modal">
    <div class="app_modal__modal-header">
      <h1>{{header}}</h1>
    </div>
    <div class="app_modal__modal-main">{{body}}</div>
    <div class="app_modal__modal-footer">{{footer}}</div>
  </div>
</div>
