import { Component } from '@angular/core';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  public isOpen = true;
}
<div class="app_modal__background {{isOpen ? '' : 'app_modal--hidden'}}">
  <div class="app_modal__modal">
    <div class="app_modal__modal-header"></div>
    <div class="app_modal__modal-main"></div>
    <div class="app_modal__modal-footer"></div>
  </div>
</div>
