import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  constructor() { }

  start = 0;
  amount = 0;
  result = 0;
  subtract() {
    console.log("subtract");
  };
  add() {
    console.log("add");
  };
}

<div class="app_modal__modal">
  <div class="app_modal__modal-header">
    <h1>Simple Calulator</h1>
  </div>
  <div class="app_modal__modal-main">
    <p class="home">Enter two numbers below then click add or subtract</p>

    <div class="home home__input">
      <label for="start">Start: </label>
      <input value="{{start}}" id="start" />
    </div>
    <div class="home home__input">
      <label for="amount">Amount: </label>
      <input value="{{amount}}" id="amount" />
    </div>

    <button class="home home__button">Add</button><button class="home__button">Subtract</button>

    <p>{{result}}</p>

  </div>
  <div class="app_modal__modal-footer"><span>Close</span></div>
</div>
