import { Component, ViewChild } from '@angular/core';
import { ModalComponent } from '../modal/modal.component';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  @ViewChild(ModalComponent) child!: ModalComponent;

  headerColor = '#1C110A';
  bodyColor = '#E7E5DF';
  footerColor = '#1C110A'

  headerTextColor = '#FFFFFF';
  bodyTextColor = '#000000';
  footerTextColor = '#FFFFFF'

  public callModal() { this.child.open(); }
}
<app-modal></app-modal>

<h1>Simple Calculator</h1>

<p>Please select the colors for your modal below, then the calculator button below.</p>
<div>
  <b>Header</b>
  <label for="header">Please select a header color</label>
  <input id="header" value="{{headerColor}}" type="color" />
</div>
<div>
  <label for="body">Please select a body color</label>
  <input id="body" value="{{bodyColor}}" type="color" />
</div>
<div>
  <label for="footer">Please select a header color</label>
  <input id="footer" value="{{footerColor}}" type="color"  />
</div>

<button (click)="callModal()">Calculator</button>
